-- OBJECTIVE 1 - EXPLORE THE ITEMS TABLE
-- The most and least expensive item
SELECT item_name, price
FROM 
	(SELECT item_name, price
    FROM menu_items
    ORDER BY price DESC
	LIMIT 1
) AS most_expensive

UNION
 
SELECT item_name, price
FROM 
	(SELECT item_name, price
    FROM menu_items
    ORDER BY price ASC
	LIMIT 1
) AS least_expensive;

-- How many Italian dishes on the menu. what are the least and most expensive Italian dishes
SELECT * FROM menu_items; 
SELECT
	item_name,
    count(menu_item_id) as numbers_of_dishes
FROM menu_items
WHERE category = 'Italian'
GROUP BY menu_item_id;

SELECT
	count(menu_item_id) as numbers_of_dishes
FROM menu_items
WHERE category = 'Italian';

SELECT item_name, category, price
FROM 
	(SELECT item_name, category, price
    FROM menu_items
    WHERE category = 'Italian'
    ORDER BY price DESC
	LIMIT 1
) AS most_expensive

UNION
 
SELECT item_name, category, price
FROM 
	(SELECT item_name, category, price
    FROM menu_items
    WHERE category = 'Italian'
    ORDER BY price ASC
	LIMIT 1
) AS least_expensive;

-- How many dishes in each category + the average price per category
SELECT * FROM menu_items;

SELECT 
	'Italian' AS category, 
    count(menu_item_id) as numbers_of_dishes
FROM menu_items
WHERE category = 'Italian'

UNION

SELECT
	'Asian' AS category, 
    count(menu_item_id) as numbers_of_dishes
FROM menu_items
WHERE category = 'Asian'

UNION

SELECT
	'Mexican' AS category, 
    count(menu_item_id) as numbers_of_dishes
FROM menu_items
WHERE category = 'Mexican'

UNION 

SELECT
	'American' AS category, 
    count(menu_item_id) as numbers_of_dishes
FROM menu_items
WHERE category = 'American';

SELECT 
	category,
    COUNT(menu_item_id) AS number_of_dishes,
    ROUND(AVG(price),2) AS average_price
FROM menu_items
WHERE category IN ('American','Asian','Mexican','Italian')
GROUP BY category
ORDER BY average_price DESC;


-- OBJECTIVE 2 - EXPLORE THE ORDERS TABLE
-- How many orders were made within this date range + How many items were ordered
SELECT 
    COUNT(DISTINCT order_id) AS numbers_of_orders
FROM order_details;

SELECT COUNT(*) FROM order_details;

-- Which orders has the most number of items
SELECT
	order_id,
	COUNT(item_id) AS numbers_of_items 
FROM order_details
GROUP BY order_id
ORDER BY numbers_of_items DESC;

-- How many orders has more than 12 items
SELECT COUNT(*) FROM
(SELECT
	order_id,
	COUNT(item_id) AS numbers_of_items 
FROM order_details
GROUP BY order_id
HAVING numbers_of_items > 12) as numbers_of_orders;

-- OBJECTIVE 3 
-- Combine 2 tables into 1 table
CREATE TABLE combined_table AS
SELECT *
FROM menu_items
JOIN order_details ON menu_items.menu_item_id = order_details.item_id;

-- What were the least and most ordered items? What categories were they in?
-- Least ordered item 
SELECT category, item_name, total_orders
FROM (
	SELECT
		category,
        item_name,
        COUNT(*) AS total_orders
	FROM combined_table
	GROUP BY category, item_name
	ORDER BY total_orders ASC
	LIMIT 1
) AS least_item

UNION

-- Most ordered item 
SELECT category, item_name, total_orders
FROM (
	SELECT
		category,
        item_name,
        COUNT(*) AS total_orders
	FROM combined_table
	GROUP BY category, item_name
	ORDER BY total_orders DESC
	LIMIT 1
) AS most_item;

SELECT 
	item_name, 
    COUNT(order_details_id) AS num_purchases
FROM combined_table 
LEFT JOIN menu_item
	ON od.item_id = mi.menu_item_id
GROUP BY item_name
ORDER BY num_purchases DESC;

